
public class Q214 {

    public static void main(String[] args) {
        System.out.println(new Q214().shortestPalindrome("abyyzba"));
//        System.out.println(new Q214().shortestPalindrome("aacecaaa"));
//        System.out.println(new Q214().shortestPalindrome("abcd"));
//        System.out.println(new Q214().shortestPalindrome("abb"));
//        System.out.println(new Q214().shortestPalindrome("aabba"));
//        System.out.println(new Q214().shortestPalindrome(""));
    }

    public String shortestPalindrome(String s) {
        int j = 0;
        for (int i = s.length() - 1; i >= 0; i--) {
            if (s.charAt(i) == s.charAt(j)) {
                j += 1;
            }
        }
        System.out.println(j);
        if (j == s.length()) {
            return s;
        }
        String suffix = s.substring(j);
        System.out.println(suffix);
        return new StringBuffer(suffix).reverse().toString() + shortestPalindrome(s.substring(0, j)) + suffix;
    }

}
