    public int threeSumMulti(int[] A, int target) {
        int mod = 1_000_000_007, max = Integer.MIN_VALUE;
        long[] fre = new long[101];
        for (int a : A) {
            fre[a]++;
            max = Math.max(max, a);
        }
        long result = 0;
        if (target % 3 == 0) {
            result += (fre[target / 3] * (fre[target / 3] - 1) * (fre[target / 3] - 2) / 6) % mod;
        }
        for (int i = 0; i <= max; i++) {
            if (target - i >= 0 && (target - i) % 2 == 0 && (target != i * 3) && (target - i) <= max * 2) {
                result += (fre[i] * fre[(target - i) / 2] * (fre[(target - i) / 2] - 1) / 2) % mod;
            }
        }
        for (int i = 0; i <= max; i++) {
            for (int j = i + 1; j <= max; j++) {
                int k = target - i - j;
                if (k > j && i != k && k >= 0 && k <= max) {
                    result += (fre[i] * fre[j] * fre[k]) % mod;
                }
            }
        }
        return (int) result;
    }
